(function($) {
    'use strict';

    /**
     * Module to allow videos to be playable in Feedly.q
     *
     * @returns {{setup: Function}}
     * @constructor
     */
    var VideoHelper = function() {
        var timeoutId;

        /**
         * Listen for changes to the DOM.
         */
        var setupEvents = function () {
            console.log("Setting up events...");
            var container = document.getElementById('box');
            container.addEventListener("DOMSubtreeModified", _handleDOMSubtreeModified, false);

            $(container).on('click', '.slideBumper', unloadVideo);
            $(container).on('keydown', function(e) {
                console.log("Detected keydown", e.keyCode);
                if (e.keyCode === 37 || e.keyCode === 39) {
                    unloadVideo();
                }
            });
        };

        /**
         * Handle "DOMSubtreeModified" event to DOM.
         * Uses setTimeout() for performance reasons-- this method is called very frequently.
         *
         * @private
         */
        var _handleDOMSubtreeModified = function() {
            if (timeoutId) {
                clearTimeout(timeoutId);
            }
            timeoutId = setTimeout(_handleDomModified, 200);
         };

         /**
         * Add controls to videos.
         *
         * @private
         */
        var _handleDomModified = function() {
            var allContent = document.getElementById('floatingEntry');
            var videos = allContent.getElementsByTagName('video');
            var video;
            Array.prototype.forEach.call(videos, function(el, i) {
                $(el).attr("controls", "controls");
                $(el).attr("autoplay", "autoplay");
                $(el).attr("autostart", "autostart");
                $(el).attr("preload", "metadata");
                $(el).attr("style", "width: 100% !important; height: auto !important;");
                $(el).prop("muted", false);
                $(el).prop("volume", 1);
            });
        };

        var unloadVideo = function() {
            console.log('Unloading video');
            var allContent = document.getElementById('floatingEntry');
            var videos = allContent.getElementsByTagName('video');
            var video;
            Array.prototype.forEach.call(videos, function(el, i) {
                video = $(el)[0];
                video.pause();
                $(video).attr('src', '');
                video.load();
                video.remove();
            });
        };

        return {
            setup: setupEvents
        };
    };

    var videoHelper = new VideoHelper();
    videoHelper.setup();
})($);